import{_ as o,c as r,o as a,ah as l}from"./~/framework.Z04FJA2X.js";const u=JSON.parse('{"title":"Snowfox Changelog","description":"","frontmatter":{},"headers":[],"relativePath":"en/snowfox/changelog/index.md","filePath":"snowfox/changelog@en.md","lastUpdated":1744238051000}'),i={name:"en/snowfox/changelog/index.md"};function t(s,e,n,d,c,h){return a(),r("div",null,e[0]||(e[0]=[l('<h1 id="snowfox-changelog" tabindex="-1">Snowfox Changelog <a class="header-anchor" href="#snowfox-changelog" aria-label="Permalink to &quot;Snowfox Changelog&quot;">​</a></h1><br><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p>Changelog of newer versions is temporarily backlogged.<br> Please refer to <a href="https://github.com/0h7z/Snowfox/releases" target="_blank" rel="noreferrer">GitHub</a> for all releases.</p></div><h2 id="v124.0.2" tabindex="-1">v124.0.2 <a class="header-anchor" href="#v124.0.2" aria-label="Permalink to &quot;v124.0.2	{#v124.0.2}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/124.0.2/releasenotes/" target="_blank" rel="noreferrer">124.0.2 Firefox Release Notes</a></li></ul><br><h2 id="v124.0.1" tabindex="-1">v124.0.1 <a class="header-anchor" href="#v124.0.1" aria-label="Permalink to &quot;v124.0.1	{#v124.0.1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/124.0.1/releasenotes/" target="_blank" rel="noreferrer">124.0.1 Firefox Release Notes</a></li></ul><br><h2 id="v124.0" tabindex="-1">v124.0.0 <a class="header-anchor" href="#v124.0" aria-label="Permalink to &quot;v124.0.0	{#v124.0}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/124.0/releasenotes/" target="_blank" rel="noreferrer">124.0.0 Firefox Release Notes</a></li><li>Completely disable bootstrap for Windows build.</li><li>Default to disable <code>dom.serviceWorkers.enabled</code>.</li><li>Default to set <code>widget.non-native-theme.scrollbar.style</code> to <code>4</code>.</li><li>Fix color-scheme for more blank/unstyled pages.</li><li>Fix layout of page <code>about:serviceworkers</code>.</li></ul><br><h2 id="v123.0.1" tabindex="-1">v123.0.1 <a class="header-anchor" href="#v123.0.1" aria-label="Permalink to &quot;v123.0.1	{#v123.0.1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/123.0.1/releasenotes/" target="_blank" rel="noreferrer">123.0.1 Firefox Release Notes</a></li><li>Default to enable <code>browser.engagement.ctrlTab.has-used</code>.</li><li>Default to enable <code>browser.engagement.downloads-button.has-used</code>.</li><li>Default to enable <code>browser.engagement.home-button.has-used</code>.</li><li>Default to enable <code>browser.engagement.library-button.has-used</code>.</li><li>Default to enable <code>browser.engagement.sidebar-button.has-used</code>.</li><li>Default to set <code>signon.firefoxRelay.feature</code> to <code>disabled</code>.</li><li>Fix behavior of module <code>RFPHelper.sys.mjs</code>.</li><li>Fix the style in <code>XMLPrettyPrint.css</code>.</li></ul><br><h2 id="v123.0" tabindex="-1">v123.0.0 <a class="header-anchor" href="#v123.0" aria-label="Permalink to &quot;v123.0.0	{#v123.0}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/123.0/releasenotes/" target="_blank" rel="noreferrer">123.0.0 Firefox Release Notes</a></li><li>Default to enable <code>devtools.inspector.showAllAnonymousContent</code>.</li><li>Fix behavior and layout of page <code>about:policies</code>.</li><li>Fix behavior of module <code>RFPHelper.sys.mjs</code>.</li><li>Fix behavior of module <code>SessionStore.sys.mjs</code>.</li><li>Fix white flashing on opaque image/background loading (upstream <a href="https://bugzilla.mozilla.org/show_bug.cgi?id=1556156" target="_blank" rel="noreferrer">Bug 1556156</a> &amp; <a href="https://bugzilla.mozilla.org/show_bug.cgi?id=1808149" target="_blank" rel="noreferrer">Bug 1808149</a>).</li></ul><br><h2 id="v122.0.1" tabindex="-1">v122.0.1 <a class="header-anchor" href="#v122.0.1" aria-label="Permalink to &quot;v122.0.1	{#v122.0.1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/122.0.1/releasenotes/" target="_blank" rel="noreferrer">122.0.1 Firefox Release Notes</a></li><li>Fix color-scheme for transparent iframe(s).</li></ul><br><h2 id="v122.0" tabindex="-1">v122.0.0 <a class="header-anchor" href="#v122.0" aria-label="Permalink to &quot;v122.0.0	{#v122.0}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/122.0/releasenotes/" target="_blank" rel="noreferrer">122.0.0 Firefox Release Notes</a></li><li>Fix color-scheme for empty/blank/unstyled pages.</li></ul><br><h2 id="v121.0.1" tabindex="-1">v121.0.1 <a class="header-anchor" href="#v121.0.1" aria-label="Permalink to &quot;v121.0.1	{#v121.0.1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/121.0.1/releasenotes/" target="_blank" rel="noreferrer">121.0.1 Firefox Release Notes</a></li><li>Default to set <code>media.default_volume</code> to <code>0.5</code>.</li></ul><br><h2 id="v121.0" tabindex="-1">v121.0.0 <a class="header-anchor" href="#v121.0" aria-label="Permalink to &quot;v121.0.0	{#v121.0}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/121.0/releasenotes/" target="_blank" rel="noreferrer">121.0.0 Firefox Release Notes</a></li></ul><br><h2 id="v120.0.1" tabindex="-1">v120.0.1 <a class="header-anchor" href="#v120.0.1" aria-label="Permalink to &quot;v120.0.1	{#v120.0.1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/120.0.1/releasenotes/" target="_blank" rel="noreferrer">120.0.1 Firefox Release Notes</a></li><li>Default to disable <code>browser.translations.enable</code>.</li><li>Default to set <code>network.http.referer.XOriginPolicy</code> to <code>0</code> (revert <a href="#v119.0-1">v119.0.0-1</a>).</li><li>Fix behavior of function <code>nsresult nsHttpHandler::Init</code>.</li><li>Fix behavior of module <code>RFPHelper.sys.mjs</code>.</li><li>Fix behavior of page <code>about:preferences</code>.</li></ul><br><h2 id="v120.0" tabindex="-1">v120.0.0 <a class="header-anchor" href="#v120.0" aria-label="Permalink to &quot;v120.0.0	{#v120.0}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/120.0/releasenotes/" target="_blank" rel="noreferrer">120.0.0 Firefox Release Notes</a></li><li>Default to set <code>image.http.accept</code> to <code>image/avif,image/webp,*/*</code>.</li><li>Default to set <code>media.cubeb.force_sample_rate</code> to <code>192000</code>.</li><li>Default to set <code>privacy.fingerprintingProtection.overrides</code> to <code>+AllTargets,-CSSPrefersColorScheme</code>.</li><li>Fix behavior of function <code>void MediaError::GetMessage</code> (revert <a href="#v118.0.2-1">v118.0.2-1</a>).</li><li>Fix behavior of function <code>void nsHttpHandler::PrefsChanged</code>.</li><li>Fix behavior of module <code>RFPHelper.sys.mjs</code>.</li><li>Fix behavior of page <code>about:firefoxview-next</code>.</li><li>Fix version number towards better consistency.</li><li>Migrate from <code>privacy.resistFingerprinting</code> to <code>privacy.fingerprintingProtection</code>.</li><li>Remove <code>network.http.useragent.forceVersion</code> (restored in <a href="#v116.0-1">v116.0.0-1</a>) due to too much upstream breakage.</li><li>Remove <code>privacy.resistFingerprinting.override-color-scheme</code> (replaced by <code>-CSSPrefersColorScheme</code>).</li></ul><br><h2 id="v119.0.1-1" tabindex="-1">v119.0.1-1 <a class="header-anchor" href="#v119.0.1-1" aria-label="Permalink to &quot;v119.0.1-1	{#v119.0.1-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/119.0.1/releasenotes/" target="_blank" rel="noreferrer">119.0.1 Firefox Release Notes</a></li></ul><br><h2 id="v119.0-1" tabindex="-1">v119.0.0-1 <a class="header-anchor" href="#v119.0-1" aria-label="Permalink to &quot;v119.0.0-1	{#v119.0-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/119.0/releasenotes/" target="_blank" rel="noreferrer">119.0.0 Firefox Release Notes</a></li><li>Default to disable <code>media.recorder.audio_node.enabled</code> (revert <a href="#v118.0.2-1">v118.0.2-1</a>).</li><li>Default to enable <code>network.http.referer.hideOnionSource</code>.</li><li>Default to set <code>network.http.referer.XOriginPolicy</code> to <code>1</code>.</li><li>Fix layout of page <code>about:policies</code>.</li></ul><br><h2 id="v118.0.2-2" tabindex="-1">v118.0.2-2 <a class="header-anchor" href="#v118.0.2-2" aria-label="Permalink to &quot;v118.0.2-2	{#v118.0.2-2}&quot;">​</a></h2><hr><ul><li>Fix layout of page <code>about:policies</code>.</li><li>Fix support of monochrome mode for <code>Save to PDF</code> printer.</li></ul><br><h2 id="v118.0.2-1" tabindex="-1">v118.0.2-1 <a class="header-anchor" href="#v118.0.2-1" aria-label="Permalink to &quot;v118.0.2-1	{#v118.0.2-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/118.0.2/releasenotes/" target="_blank" rel="noreferrer">118.0.2 Firefox Release Notes</a></li><li>Default to disable <code>browser.translations.automaticallyPopup</code>.</li><li>Default to enable <code>media.recorder.audio_node.enabled</code>.</li><li>Default to set <code>media.recorder.max_memory</code> to <code>8388608</code>.</li><li>Fix behavior of function <code>void MediaError::GetMessage</code>.</li></ul><br><h2 id="v118.0.1-1" tabindex="-1">v118.0.1-1 <a class="header-anchor" href="#v118.0.1-1" aria-label="Permalink to &quot;v118.0.1-1	{#v118.0.1-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/118.0/releasenotes/" target="_blank" rel="noreferrer">118.0.0 Firefox Release Notes</a></li><li><a href="https://www.mozilla.org/firefox/118.0.1/releasenotes/" target="_blank" rel="noreferrer">118.0.1 Firefox Release Notes</a></li></ul><br><h2 id="v117.0.1-1" tabindex="-1">v117.0.1-1 <a class="header-anchor" href="#v117.0.1-1" aria-label="Permalink to &quot;v117.0.1-1	{#v117.0.1-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/117.0.1/releasenotes/" target="_blank" rel="noreferrer">117.0.1 Firefox Release Notes</a></li><li>Default to set <code>media.videocontrols.picture-in-picture.video-toggle.min-video-secs</code> to <code>10</code>.</li><li>Fix metadata of generated PDF (upstream <a href="https://bugzilla.mozilla.org/show_bug.cgi?id=1830086" target="_blank" rel="noreferrer">Bug 1830086</a>).</li></ul><br><h2 id="v117.0-1" tabindex="-1">v117.0.0-1 <a class="header-anchor" href="#v117.0-1" aria-label="Permalink to &quot;v117.0.0-1	{#v117.0-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/117.0/releasenotes/" target="_blank" rel="noreferrer">117.0.0 Firefox Release Notes</a></li><li>Build with official Clang instead of Mozilla Clang for Windows build.</li><li>Improve the default CJK font list.</li></ul><br><h2 id="v116.0.3-1" tabindex="-1">v116.0.3-1 <a class="header-anchor" href="#v116.0.3-1" aria-label="Permalink to &quot;v116.0.3-1	{#v116.0.3-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/116.0.3/releasenotes/" target="_blank" rel="noreferrer">116.0.3 Firefox Release Notes</a></li><li>Decrease the precision of BuildID.</li><li>Improve the default CJK font list.</li></ul><br><h2 id="v116.0.2-1" tabindex="-1">v116.0.2-1 <a class="header-anchor" href="#v116.0.2-1" aria-label="Permalink to &quot;v116.0.2-1	{#v116.0.2-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/116.0.1/releasenotes/" target="_blank" rel="noreferrer">116.0.1 Firefox Release Notes</a></li><li><a href="https://www.mozilla.org/firefox/116.0.2/releasenotes/" target="_blank" rel="noreferrer">116.0.2 Firefox Release Notes</a></li></ul><br><h2 id="v116.0-1" tabindex="-1">v116.0.0-1 <a class="header-anchor" href="#v116.0-1" aria-label="Permalink to &quot;v116.0.0-1	{#v116.0-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/115.0.3/releasenotes/" target="_blank" rel="noreferrer">115.0.3 Firefox Release Notes</a></li><li><a href="https://www.mozilla.org/firefox/116.0/releasenotes/" target="_blank" rel="noreferrer">116.0.0 Firefox Release Notes</a></li><li>Default to enable <code>browser.meta_refresh_when_inactive.disabled</code>.</li><li>Remove <code>network.http.useragent.forceRVOnly</code>.</li><li>Restore <code>network.http.useragent.forceVersion</code> (upstream <a href="https://bugzilla.mozilla.org/show_bug.cgi?id=1837343" target="_blank" rel="noreferrer">Bug 1837343</a>).</li></ul><br><h2 id="v115.0.2-1" tabindex="-1">v115.0.2-1 <a class="header-anchor" href="#v115.0.2-1" aria-label="Permalink to &quot;v115.0.2-1	{#v115.0.2-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/115.0.2/releasenotes/" target="_blank" rel="noreferrer">115.0.2 Firefox Release Notes</a></li></ul><br><h2 id="v115.0.1-1" tabindex="-1">v115.0.1-1 <a class="header-anchor" href="#v115.0.1-1" aria-label="Permalink to &quot;v115.0.1-1	{#v115.0.1-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/114.0.2/releasenotes/" target="_blank" rel="noreferrer">114.0.2 Firefox Release Notes</a></li><li><a href="https://www.mozilla.org/firefox/115.0/releasenotes/" target="_blank" rel="noreferrer">115.0.0 Firefox Release Notes</a></li><li><a href="https://www.mozilla.org/firefox/115.0.1/releasenotes/" target="_blank" rel="noreferrer">115.0.1 Firefox Release Notes</a></li><li>Improve the workflow for Windows build.</li></ul><br><h2 id="v114.0.1-1" tabindex="-1">v114.0.1-1 <a class="header-anchor" href="#v114.0.1-1" aria-label="Permalink to &quot;v114.0.1-1	{#v114.0.1-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/113.0.2/releasenotes/" target="_blank" rel="noreferrer">113.0.2 Firefox Release Notes</a></li><li><a href="https://www.mozilla.org/firefox/114.0/releasenotes/" target="_blank" rel="noreferrer">114.0.0 Firefox Release Notes</a></li><li><a href="https://www.mozilla.org/firefox/114.0.1/releasenotes/" target="_blank" rel="noreferrer">114.0.1 Firefox Release Notes</a></li><li>Fix layout of page <code>about:policies</code>.</li></ul><br><h2 id="v113.0.1-1" tabindex="-1">v113.0.1-1 <a class="header-anchor" href="#v113.0.1-1" aria-label="Permalink to &quot;v113.0.1-1	{#v113.0.1-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/113.0.1/releasenotes/" target="_blank" rel="noreferrer">113.0.1 Firefox Release Notes</a></li><li>Default to come with the QR code functionality.</li><li>Default to enable <code>general.useragent.compatMode.firefox</code>.</li><li>Improve the <code>PDFDocumentProperties</code> in builtin PDF.js.</li></ul><br><h2 id="v113.0-1" tabindex="-1">v113.0.0-1 <a class="header-anchor" href="#v113.0-1" aria-label="Permalink to &quot;v113.0.0-1	{#v113.0-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/113.0/releasenotes/" target="_blank" rel="noreferrer">113.0.0 Firefox Release Notes</a></li><li>Fix the broken useragent when <code>privacy.resistFingerprinting</code> is disabled.</li></ul><br><h2 id="v112.0.2-3" tabindex="-1">v112.0.2-3 <a class="header-anchor" href="#v112.0.2-3" aria-label="Permalink to &quot;v112.0.2-3	{#v112.0.2-3}&quot;">​</a></h2><hr><ul><li>Fix and improve OS integration for Windows install.</li><li>Fix the <code>NativeMessagingHosts</code> problem on Windows.</li><li>Fix the installer and uninstaller for Windows.</li></ul><br><h2 id="v112.0.2-2" tabindex="-1">v112.0.2-2 <a class="header-anchor" href="#v112.0.2-2" aria-label="Permalink to &quot;v112.0.2-2	{#v112.0.2-2}&quot;">​</a></h2><hr><ul><li>Fix the broken <code>PDFDocumentProperties</code> in builtin PDF.js.</li><li>Improve the workflow for Windows build.</li></ul><br><h2 id="v112.0.2-1" tabindex="-1">v112.0.2-1 <a class="header-anchor" href="#v112.0.2-1" aria-label="Permalink to &quot;v112.0.2-1	{#v112.0.2-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/112.0.2/releasenotes/" target="_blank" rel="noreferrer">112.0.2 Firefox Release Notes</a></li><li>Rewrite the whole build process for Windows (due to <a href="https://github.com/Heptazhou/Librewolf-Windows" target="_blank" rel="noreferrer">Librewolf-Windows</a> is dead).</li></ul><br><h2 id="v112.0.1-1" tabindex="-1">v112.0.1-1 <a class="header-anchor" href="#v112.0.1-1" aria-label="Permalink to &quot;v112.0.1-1	{#v112.0.1-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/112.0.1/releasenotes/" target="_blank" rel="noreferrer">112.0.1 Firefox Release Notes</a></li><li>Add support for JXL (JPEG XL) animated image (upstream <a href="https://bugzilla.mozilla.org/show_bug.cgi?id=1709818" target="_blank" rel="noreferrer">Bug 1709818</a>).</li><li>Add support of color profile for JXL (upstream <a href="https://bugzilla.mozilla.org/show_bug.cgi?id=1709814" target="_blank" rel="noreferrer">Bug 1709814</a>).</li><li>Add support of progressive decoding for JXL (upstream <a href="https://bugzilla.mozilla.org/show_bug.cgi?id=1709815" target="_blank" rel="noreferrer">Bug 1709815</a>).</li><li>Add support of transparency for JXL (upstream <a href="https://bugzilla.mozilla.org/show_bug.cgi?id=1709816" target="_blank" rel="noreferrer">Bug 1709816</a>).</li></ul><br><h2 id="v112.0-1" tabindex="-1">v112.0.0-1 <a class="header-anchor" href="#v112.0-1" aria-label="Permalink to &quot;v112.0.0-1	{#v112.0-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/112.0/releasenotes/" target="_blank" rel="noreferrer">112.0.0 Firefox Release Notes</a></li><li>Default to set <code>network.http.useragent.forceRVOnly</code> to <code>0</code>.</li></ul><br><h2 id="v111.0.1-1" tabindex="-1">v111.0.1-1 <a class="header-anchor" href="#v111.0.1-1" aria-label="Permalink to &quot;v111.0.1-1	{#v111.0.1-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/111.0.1/releasenotes/" target="_blank" rel="noreferrer">111.0.1 Firefox Release Notes</a></li></ul><br><h2 id="v111.0-3" tabindex="-1">v111.0.0-3 <a class="header-anchor" href="#v111.0-3" aria-label="Permalink to &quot;v111.0.0-3	{#v111.0-3}&quot;">​</a></h2><hr><ul><li>Now build with <code>--with-app-name=snowfox</code> instead of <code>--with-app-name=firefox</code> for Windows.</li><li>Now possible to use <code>private_browsing.exe</code> on Windows.</li></ul><br><h2 id="v111.0-2" tabindex="-1">v111.0.0-2 <a class="header-anchor" href="#v111.0-2" aria-label="Permalink to &quot;v111.0.0-2	{#v111.0-2}&quot;">​</a></h2><hr><ul><li>Default to disable <code>media.eme.showBrowserMessage</code>.</li><li>Default to enable <code>image.avif.force-loop</code>.</li><li>Fix behavior of function <code>nsAVIFDecoder::DecodeResult nsAVIFDecoder::Decode</code>.</li><li>Fix behavior of function <code>uint32_t RuntimeService::ClampedHardwareConcurrency</code>.</li><li>Now possible to restore EME notification message shown when DRM is disabled.</li></ul><br><h2 id="v111.0-1" tabindex="-1">v111.0.0-1 <a class="header-anchor" href="#v111.0-1" aria-label="Permalink to &quot;v111.0.0-1	{#v111.0-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/111.0/releasenotes/" target="_blank" rel="noreferrer">111.0.0 Firefox Release Notes</a></li></ul><br><h2 id="v110.0.1-1" tabindex="-1">v110.0.1-1 <a class="header-anchor" href="#v110.0.1-1" aria-label="Permalink to &quot;v110.0.1-1	{#v110.0.1-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/110.0.1/releasenotes/" target="_blank" rel="noreferrer">110.0.1 Firefox Release Notes</a></li><li>Rewrite the build script for Windows.</li></ul><br><h2 id="v110.0-2" tabindex="-1">v110.0.0-2 <a class="header-anchor" href="#v110.0-2" aria-label="Permalink to &quot;v110.0.0-2	{#v110.0-2}&quot;">​</a></h2><hr><ul><li>Add support for AVIF (AV1 Image) animated image (upstream <a href="https://bugzilla.mozilla.org/show_bug.cgi?id=1788119" target="_blank" rel="noreferrer">v111</a>:<a href="https://bugzilla.mozilla.org/show_bug.cgi?id=1825580" target="_blank" rel="noreferrer">v113</a>).</li><li>Default to enable <code>image.avif.sequence.animate_avif_major_branded_images</code>.</li><li>Default to enable <code>image.avif.sequence.enabled</code>.</li><li>Remove EME notification message shown when DRM is disabled. (about <a href="https://en.wikipedia.org/w/index.php?curid=18938226" target="_blank" rel="noreferrer">DRM</a> / <a href="https://en.wikipedia.org/w/index.php?curid=42965137" target="_blank" rel="noreferrer">EME</a>)</li></ul><br><h2 id="v110.0-1" tabindex="-1">v110.0.0-1 <a class="header-anchor" href="#v110.0-1" aria-label="Permalink to &quot;v110.0.0-1	{#v110.0-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/110.0/releasenotes/" target="_blank" rel="noreferrer">110.0.0 Firefox Release Notes</a></li><li>Default to disable <code>devtools.performance.new-panel-onboarding</code>.</li></ul><br><h2 id="v109.0.1-3" tabindex="-1">v109.0.1-3 <a class="header-anchor" href="#v109.0.1-3" aria-label="Permalink to &quot;v109.0.1-3	{#v109.0.1-3}&quot;">​</a></h2><hr><ul><li>Default to disable <code>devtools.screenshot.audio.enabled</code>.</li><li>Default to enable <code>devtools.popups.debug</code>.</li><li>Default to set <code>media.peerconnection.dtls.version.max</code> to <code>772</code> (TLS v1.3).</li><li>Default to set <code>media.videocontrols.picture-in-picture.video-toggle.has-used</code> to <code>true</code>.</li><li>Default to set <code>media.videocontrols.picture-in-picture.video-toggle.min-video-secs</code> to <code>60</code>.</li><li>Fix the screenshot functionality in DevTools.</li></ul><br><h2 id="v109.0.1-2" tabindex="-1">v109.0.1-2 <a class="header-anchor" href="#v109.0.1-2" aria-label="Permalink to &quot;v109.0.1-2	{#v109.0.1-2}&quot;">​</a></h2><hr><ul><li>Default to disable OffscreenCanvas for improved performance and compatibility.</li><li>Fix behavior of function <code>static nsSize GetDeviceSize</code>.</li></ul><br><h2 id="v109.0.1-1" tabindex="-1">v109.0.1-1 <a class="header-anchor" href="#v109.0.1-1" aria-label="Permalink to &quot;v109.0.1-1	{#v109.0.1-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/109.0.1/releasenotes/" target="_blank" rel="noreferrer">109.0.1 Firefox Release Notes</a></li><li>Fix the Performance API (upstream <a href="https://bugzilla.mozilla.org/show_bug.cgi?id=1811567" target="_blank" rel="noreferrer">v111</a>) for improved compatibility.</li></ul><br><h2 id="v109.0-5" tabindex="-1">v109.0.0-5 <a class="header-anchor" href="#v109.0-5" aria-label="Permalink to &quot;v109.0.0-5	{#v109.0-5}&quot;">​</a></h2><hr><ul><li>Add support for JXL (JPEG XL) still image.</li><li>Fix behavior of function <code>static nsCString ImageAcceptHeader</code>.</li><li>Reduce the size of source archive significantly.</li></ul><br><h2 id="v109.0-4" tabindex="-1">v109.0.0-4 <a class="header-anchor" href="#v109.0-4" aria-label="Permalink to &quot;v109.0.0-4	{#v109.0-4}&quot;">​</a></h2><hr><ul><li>Default to disable <code>devtools.accessibility.enabled</code>.</li><li>Default to disable <code>layout.word_select.eat_space_to_next_word</code>.</li><li>Default to enable <code>alerts.showFavicons</code>.</li><li>Default to enable CRLite and disable OCSP, for improved performance, privacy, and even security.</li><li>Default to set <code>security.tls.ech.grease_probability</code> to <code>50</code>, for slightly improved security.</li><li>Reduce the delay of fullscreen transition perceivably.</li></ul><br><h2 id="v109.0-3" tabindex="-1">v109.0.0-3 <a class="header-anchor" href="#v109.0-3" aria-label="Permalink to &quot;v109.0.0-3	{#v109.0-3}&quot;">​</a></h2><hr><ul><li>Default to disable <code>general.autoScroll</code>.</li><li>Default to enable <code>browser.menu.showViewImageInfo</code>.</li><li>Default to enable <code>browser.preferences.experimental</code>.</li><li>Default to remove CJK from list <code>browser.tabs.secondaryTextUnsupportedLocales</code>.</li><li>Default to set <code>dom.script_loader.delazification.strategy</code> to <code>3</code>.</li><li>Fix the style in <code>TopLevelImageDocument.css</code>.</li></ul><br><h2 id="v109.0-2" tabindex="-1">v109.0.0-2 <a class="header-anchor" href="#v109.0-2" aria-label="Permalink to &quot;v109.0.0-2	{#v109.0-2}&quot;">​</a></h2><hr><ul><li>Default to enable <code>browser.tabs.insertAfterCurrent</code>.</li><li>Default to enable <code>devtools.webconsole.persistlog</code>.</li><li>Default to enable <code>permissions.desktop-notification.notNow.enabled</code>.</li><li>Fix behavior of function <code>uint32_t RuntimeService::ClampedHardwareConcurrency</code>.</li><li>Fix behavior of the unified extensions button.</li><li>Fix the document icon on Windows.</li></ul><br><h2 id="v109.0-1" tabindex="-1">v109.0.0-1 <a class="header-anchor" href="#v109.0-1" aria-label="Permalink to &quot;v109.0.0-1	{#v109.0-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/109.0/releasenotes/" target="_blank" rel="noreferrer">109.0.0 Firefox Release Notes</a></li><li>Default to disable <code>dom.push.connection.enabled</code>.</li><li>Default to disable <code>webgl.enable-renderer-query</code>.</li><li>Default to enable <code>browser.download.alwaysOpenPanel</code>.</li><li>Default to enable <code>browser.download.useDownloadDir</code>.</li><li>Default to enable <code>browser.urlbar.showSearchTerms.featureGate</code>.</li><li>Default to enable WebGL for improved performance and compatibility.</li><li>Default to set <code>devtools.selfxss.count</code> to <code>5</code>.</li><li>Default to set <code>network.http.useragent.forceVersion</code> to <code>108</code>.</li><li>Fix behavior of function <code>nsresult nsScreen::GetAvailRect</code>.</li><li>Fix behavior of function <code>nsresult nsScreen::GetRect</code>.</li><li>Fix behavior of function <code>void nsRFPService::GetSpoofedUserAgent</code>.</li></ul><br><h2 id="v108.0.2-1" tabindex="-1">v108.0.2-1 <a class="header-anchor" href="#v108.0.2-1" aria-label="Permalink to &quot;v108.0.2-1	{#v108.0.2-1}&quot;">​</a></h2><hr><ul><li><a href="https://www.mozilla.org/firefox/108.0/releasenotes/" target="_blank" rel="noreferrer">108.0.0 Firefox Release Notes</a></li><li><a href="https://www.mozilla.org/firefox/108.0.1/releasenotes/" target="_blank" rel="noreferrer">108.0.1 Firefox Release Notes</a></li><li><a href="https://www.mozilla.org/firefox/108.0.2/releasenotes/" target="_blank" rel="noreferrer">108.0.2 Firefox Release Notes</a></li><li>Default to enable <code>dom.security.https_first</code> and <code>dom.security.https_only_mode</code> for improved security.</li><li>Default to enable <code>privacy.resistFingerprinting</code> for improved privacy.</li><li>Default to enable the dark theme.</li><li>Fix behavior of function <code>ColorScheme Document::PreferredColorScheme</code>.</li><li>Fix behavior of function <code>uint32_t PreferredSampleRate</code>.</li><li>Fix behavior of function <code>void nsRFPService::GetSpoofedUserAgent</code>.</li></ul><br>',191)]))}const b=o(i,[["render",t]]);export{u as __pageData,b as default};
